script:
frappe.ui.form.on('Purchase Receipt Item', {
	item_code(frm, cdt, cdn) {
		 const row = locals[cdt][cdn];
		 frappe.call({
            method: "quality_control.overrides.item.get_item_default_quality_status",
            args: {
                item: row.item_code
            },
            callback: function(response) {
                if (response.message) {
                    row.quality_status = response.message;
                    row.from_quality_status = response.message;
                    frm.refresh_field("items")
                }
            }
        });
	}
})

frappe.ui.form.on('Item', {
	item_group: function(frm) {
        if (frm.doc.item_group) {
            // Fetch the 'custom_control_quality' from the selected Item Group
            frappe.db.get_value('Item Group', frm.doc.item_group, 'custom_control_quality')
                .then(response => {
                    const custom_control_quality = response.message.custom_control_quality;

                    // Set the fetched value in the current form
                    frm.set_value('custom_control_quality', custom_control_quality);
                })
                .catch(error => {
                    console.error('Error fetching custom_control_quality:', error);
                });
        }
    }
})

frappe.ui.form.on('Quality Inspection', {
	custom_process_qty(frm) {
		if (frm.doc.custom_conversion_factor) {
			let new_val = flt(frm.doc.custom_process_qty) / flt(frm.doc.custom_conversion_factor);
			if (new_val !== frm.doc.custom_process_qty_ct) {
				frm.set_value("custom_process_qty_ct", new_val);
			}
		}
	},
	custom_process_qty_ct(frm) {
		if (frm.doc.custom_conversion_factor) {
			let new_val = flt(frm.doc.custom_process_qty_ct) * flt(frm.doc.custom_conversion_factor);
			if (new_val !== frm.doc.custom_process_qty) {
				frm.set_value("custom_process_qty", new_val);
			}
		}
	},
});
